INSERT INTO `rex_base_quality_check` (`id`, `title`, `second_title`, `group`, `subgroup`, `description`, `source`, `links`, `prio`, `status`, `check`) VALUES
(7,	'Debug Modus',	'Ist der Debug Modus ausgeschaltet?',	'3',	'14',	'<p>Mit dem Debug-Modus werden bei PHP-Skriptfehlern im Frontend und Backend zusätzliche Angaben zum Fehler ausgegeben. Außerdem werden keine immutable cache-http-header verwendet. Der Debug-Modus sollte im Live-Betrieb nicht aktiviert werden, da die Fehlermeldungen auch sensible Daten beinhalten können. Darüber hinaus wird die Webpräsenz auf noindex gesetzt und ggf. aus dem Index diverser Suchmaschinen entfernt.</p><p><strong>Der Debug Modus wird im Live Mode automatisch deaktiviert.</strong></p>',	'',	'',	2,	'1',	0),
(8,	'Ungenutzte AddOns',	'Sind alle ungenutzen AddOns deinstalliert und gelöscht?',	'3',	'14',	'<p>Diese AddOns werden vermutlich nicht für den Live Betrieb der Website benötigt:</p><ul><li>Adminer / AdminerEvo</li><li>Base Quality Check</li><li>Blaupause</li><li>ICEcoder</li><li>Modulsammlung</li><li>RexStan</li><li>Textile zu HTML-Migrator</li><li>…</li></ul><p>&nbsp;</p>',	'',	'',	4,	'1',	0),
(9,	'Backup Cronjob',	'Ist ein Backup Cronjob eingerichtet?',	'2',	'13',	'',	'',	'',	20,	'1',	0),
(10,	'Error Log',	'Ist das Error Log leer?',	'2',	'13',	'',	'',	'',	21,	'1',	0),
